mapper.select:
SELECT a.*
 FROM sso_role_api ra, sso_api a

     ra.API_ID = a.ID

         and ra.role_id = #{roleId}


         and a.client_id=#{clientId}


  and ra.ENABLE_FLAG = 1

SELECT count(*) from sso_api WHERE CLIENT_ID = #{clientId} and API_NAME = #{apiName} and ENABLE_FLAG = 1
AND PARENT_ID  = #{moduleId}

    AND ID<>#{functionId,jdbcType=INTEGER}

SELECT api1.ID AS moduleId, api1.API_NAME AS moduleName, api1.CLIENT_ID AS moduleClientId, api2.ID AS functionId, api2.CLIENT_ID AS functionClientIdId, api2.API_NAME AS functionName, api2.TYPE, k.URI AS apiUrlList FROM sso_api AS api1 LEFT JOIN sso_api AS api2 ON api1.ID = api2.PARENT_ID AND api2.ENABLE_FLAG = 1 LEFT JOIN sso_api_uri j ON api2.ID = j.API_ID AND j.ENABLE_FLAG = 1 LEFT JOIN sso_uri k ON j.URI_ID = k.ID AND k.ENABLE_FLAG = 1 WHERE api1.CLIENT_ID = #{clientID}
AND api1.ID=#{moduleId}
AND api1.PARENT_ID = 0 AND api1.ENABLE_FLAG = 1 ORDER BY CONVERT (api1.API_NAME USING gbk), CONVERT (api2.API_NAME USING gbk)

mapper.update:
        UPDATE sso_uri i
        LEFT JOIN sso_api_uri j ON i.ID = j.URI_ID
        AND j.ENABLE_FLAG = 1
        SET i.ENABLE_FLAG = 0,i.UPDATE_TIME = now(),i.UPDATER_ID= #{userId}
        WHERE
	    j.API_ID = #{functionId}
